com.cloudbees.plugins.flow.BuildFlow.dsl:
def settings = env.FIHEALTH_WORKSPACE + '/'+ env.FIHEALTH_SANITY_PROJECT + '/etc/settings.json'
settings=settings.replace('$JENKINS_HOME',System.getProperty('JENKINS_HOME'))

def inputFile = new File(settings)
def inputJSON = new groovy.json.JsonSlurper().parseText(inputFile.text)

def jobs=[]
inputJSON.region_configuration.keySet().each { it ->
        def region = it
        jobs.add({build ( "FIHealth-SanityCheck-2-Exec-Region", OS_REGION_NAME: region)})
}
parallel(jobs)
